import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem("token"); // Check the user's authentication status

  const [isEditable, setIsEditable] = useState(false);

  // If the user is not authenticated, redirect them to the login page
  if (!isAuthenticated) {
    navigate("/login");
  }

  // Sample user information (you can replace this with actual user data)
  const user = {
    name: "Mohit Das",
    address: "Subhashnagar, Barasat, Kolkata - 700121",
    phoneNumber: "+91 7003961588",
    age: "20",
    gender: "Male",
    department: "BCA",
    // Add more user details here
  };

  const handleEditClick = () => {
    setIsEditable(!isEditable); // Toggle the isEditable state
  };

  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-md-4">
          <div className="card">
            <img src="./Mohit.jpg" alt="Profile" className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{user.name}</h5>
              <p className="card-text">Your Bio</p>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">My Profile</h2>
              <form>
                <div className="mb-3">
                  <label htmlFor="nameInput" className="form-label">
                    Name:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nameInput"
                    defaultValue={user.name}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="addressInput" className="form-label">
                    Address:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="addressInput"
                    defaultValue={user.address}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="phoneNumberInput" className="form-label">
                    Phone Number:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="phoneNumberInput"
                    defaultValue={user.phoneNumber}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="ageInput" className="form-label">
                    Age:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="ageInput"
                    defaultValue={user.age}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="genderInput" className="form-label">
                    Gender:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="genderInput"
                    defaultValue={user.gender}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="departmentInput" className="form-label">
                    Department:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="departmentInput"
                    defaultValue={user.department}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="text-center mt-3">
                  <div className="d-flex justify-content-between align-items-center">
                    <a
                      href="#"
                      className={`btn btn-primary`}
                      onClick={handleEditClick}
                      style={{
                        display: "inline-block",
                        order: isEditable ? "2" : "1", // Change order based on isEditable
                      }}
                    >
                      {isEditable ? "Save Changes" : "Edit Profile"}
                    </a>
                    {/* This placeholder maintains space */}
                    <span style={{ visibility: isEditable ? "hidden" : "visible" }}></span>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
