import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './register.css';

const AddItem = () => {
  const [inputdata, setInputData] = useState({
    itemName: '',
    category: '', // Add this field for item category
    description: '',
    location: '',
  });

  const [status, setStatus] = useState('Active'); // You can remove this if not needed
  const [image, setImage] = useState('');
  const [preview, setPreview] = useState('');

  const categoryOptions = [
    { value: 'Watch', label: 'Watch' },
    { value: 'Purse/Money Bag', label: 'Purse/Money Bag' },
    { value: 'Cards', label: 'Cards' },
    { value: 'Phone', label: 'Phone' },
    // Add more item categories as needed
  ];

  const setInputValue = (e) => {
    const { name, value } = e.target;
    setInputData({ ...inputdata, [name]: value });
  };

  const setCategoryValue = (e) => {
    setInputData({ ...inputdata, category: e.value });
  };

  const setProfile = (e) => {
    setImage(e.target.files[0]);
  };

  const submitItemData = async (e) => {
    e.preventDefault();

    const { itemName, category, description, location } = inputdata;
    if (itemName === '') {
      toast.error('Item name is Required!');
    } else if (category === '') {
      toast.error('Category is Required!');
    } else if (description === '') {
      toast.error('Description is Required!');
    } else if (location === '') {
      toast.error('Location is Required!');
    } else if (image === '') {
      toast.error('Image is Required!');
    } else {
      toast.success('ITEM HAS BEEN SUCCESSFULLY ADDED');
    }
  };

  useEffect(() => {
    if (image) {
      setPreview(URL.createObjectURL(image));
    }
  }, [image]);

  return (
    <>
      <div className="container">
        <h2 className="text-center mt1">Add Lost or Found Item</h2>
        <Card className="shadow mt-3 p-3">
          <div className="profile_div text-center">
            <img src={preview ? preview : '/item-placeholder.png'} alt="img" />
          </div>

          <Form>
            <Row>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Item Name</Form.Label>
                <Form.Control type="text" name="itemName" value={inputdata.itemName} onChange={setInputValue} placeholder="Enter Item Name" />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Category</Form.Label>
                <Select options={categoryOptions} onChange={setCategoryValue} />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Description</Form.Label>
                <Form.Control as="textarea" rows={3} name="description" value={inputdata.description} onChange={setInputValue} placeholder="Enter Item Description" />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Location</Form.Label>
                <Form.Control type="text" name="location" value={inputdata.location} onChange={setInputValue} placeholder="Enter Location" />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Item Image</Form.Label>
                <Form.Control type="file" name="item_image" onChange={setProfile} placeholder="Upload Item Image" />
              </Form.Group>

              <Button variant="primary" type="submit" onClick={submitItemData}>
                Submit
              </Button>
            </Row>
          </Form>
        </Card>
        <ToastContainer position="top-center" />
      </div>
    </>
  );
};

export default AddItem;
