import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios"; // Import Axios for API requests
import { Fragment } from "react";

const EmailVerify = () => {
  const { id, token } = useParams();
  const [validUrl, setValidUrl] = useState(true);

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        // Make an API call to verify the email
        const response = await axios.get(`http://localhost:8080/api/users/${id}/verify/${token}`);
        console.log(response.data); // Log the response data
        setValidUrl(true); // Set validUrl state to true if successful
      } catch (error) {
        console.error(error);
        setValidUrl(false); // Set validUrl state to false if there's an error
      }
    };

    verifyEmail(); // Call the email verification function
  }, [id, token]);

  return (
    <div>
      {validUrl ? (
        <Fragment>
          {/* Email verified successfully */}
          <h1>Email Verified Successfully</h1>
          {/* Additional content or redirection upon successful verification */}
        </Fragment>
      ) : (
        <h1>Invalid Verification Link</h1>
      )}
    </div>
  );
};

export default EmailVerify;
