import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "../Headers/headers.css";

const handleLogout = () => {
  localStorage.removeItem("token");
  window.location.reload();
};

const AuthenticatedNav = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="menu transparent-menu">
      <div className="name"></div>
      <nav className="menuitems">
        <ul className="nav-list">
          <li>
            <NavLink to="/ItemList" className="nav-menu nav-menu-animated">
              <i className="fa-solid fa-circle-info"></i>Lost & Found
            </NavLink>
          </li>
          <li>
            <NavLink to="/Contact" className="nav-menu nav-menu-animated">
              <i className="fa-solid fa-image"></i>Contact Us
            </NavLink>
          </li>
          <li>
            <div className="custom-dropdown">
              <div className="dropdown-toggle" onClick={toggleDropdown}>
                <i className="fa-solid fa-user navbar-icon"></i>
                <i className="fa-solid fa-caret-down"></i>
              </div>
              {isDropdownOpen && (
                <ul className="dropdown-menu">
                  <li>
                    <a href="/profile" onClick={toggleDropdown}>
                      My Profile
                    </a>
                  </li>
                  <li>
                    <a href="/gallary" onClick={toggleDropdown}>
                      My Lost and Found Items
                    </a>
                  </li>
                  <li>
                    <a href="#" onClick={handleLogout}>
                      Logout
                    </a>
                  </li>
                </ul>
              )}
            </div>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default AuthenticatedNav;
