import React from 'react';
import { useNavigate } from 'react-router-dom';
import './profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token'); // Check the user's authentication status

  // If the user is not authenticated, redirect them to the login page
  if (!isAuthenticated) {
    navigate('/login');
  }

  // Sample user information (you can replace this with actual user data)
  const user = {
    name: 'Mohit Das',
    address: 'Subhashnagar, Barasat, Kolkata - 700121',
    phoneNumber: '+91 7003961588',
    age:'20',
    gender:'Male',
    department:'BCA'
    // Add more user details here
  };

  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-md-4">
          <div className="card">
            <img src="./Mohit.jpg" alt="Profile" className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{user.name}</h5>
              <p className="card-text">Your Bio</p>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">My Profile</h2>
              <ul className="list-group">
                <li className="list-group-item">
                  <strong>Name : </strong> {user.name}
                </li>
                <li className="list-group-item">
                  <strong>Address : </strong> {user.address}
                </li>
                <li className="list-group-item">
                  <strong>Phone Number :</strong> {user.phoneNumber}
                </li>
                <li className="list-group-item">
                <strong>Age : </strong> {user.age}
                </li>
                <li className="list-group-item">
                 <strong>Gender:</strong>{user.gender}
                </li>
                <li className="list-group-item">
                  <strong>Department: </strong>{user.department}
                </li>

              </ul>
              <a href="#" className="btn btn-primary">Edit Profile</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
