import React from "react";
import UnauthenticatedNav from '../Navbar/UnauthenticatedNav';
import AuthenticatedNav from '../Navbar/AuthenticatedNav';

const navbarStyle = {
  backgroundColor: "#385A64",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  padding: "10px",
};

const buttonStyle = {
  color: "#385A64", // Set the text color to match the navbar color
  textDecoration: "none",
};

const Headers = () => {
  const isAuthenticated = !!localStorage.getItem("token");

  return (
    <div style={navbarStyle}>
      <div className="name">
        <h3>LOST AND FOUND SYSTEM</h3>
      </div>
      <nav className="menuitems">
        {isAuthenticated ? (
          <AuthenticatedNav buttonStyle={buttonStyle} />
        ) : (
          <UnauthenticatedNav buttonStyle={buttonStyle} />
        )}
      </nav>
    </div>
  );
};

export default Headers;
