import React, { useState, useE } from "react";
import { useNavigate } from "react-router-dom";
import "./profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem("token"); // Check the user's authentication status

  const [isEditable, setIsEditable] = useState(false);
  const [userData, setUserData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    // ... other fields you have in the database
  });

  // If the user is not authenticated, redirect them to the login page
  useEffect(() => {
    // Function to fetch user data when the profile page loads
    const fetchUserData = async () => {
      try {
        // Replace this with your API call to fetch user data from your backend
        const response = await fetch("/api/userData"); // Endpoint to fetch user data
        const data = await response.json();
        setUserData(data); // Set the user data received from the API
      } catch (error) {
        // Handle fetch error
        console.error("Error fetching user data:", error);
      }
    };

    if (isAuthenticated) {
      fetchUserData(); // Fetch user data if the user is authenticated
    } else {
      navigate("/login");
    }
  }, [isAuthenticated, navigate]);

  // Sample user information (you can replace this with actual user data)
  const user = {
    name: "Mohit Das",
    address: "Subhashnagar, Barasat, Kolkata - 700121",
    phoneNumber: "+91 7003961588",
    email: "mohit@example.com",
    dob: "1990-01-01",
    gender: "Male",
    department: "BCA",
    // Add more user details here
  };

  const handleEditClick = () => {
    setIsEditable(!isEditable); // Toggle the isEditable state
  };

  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-md-4">
          <div className="card">
            <img src="./Mohit.jpg" alt="Profile" className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{user.name}</h5>
              <p className="card-text">Your Bio</p>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">My Profile</h2>
              <form>
                <div className="mb-3">
                  <label htmlFor="nameInput" className="form-label">
                    Name :
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nameInput"
                    defaultValue={user.name}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="addressInput" className="form-label">
                    Address :
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="addressInput"
                    defaultValue={user.address}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="phoneNumberInput" className="form-label">
                    Phone Number :
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="phoneNumberInput"
                    defaultValue={user.phoneNumber}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="emailInput" className="form-label">
                    Email:
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="emailInput"
                    defaultValue={user.email}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="dobInput" className="form-label">
                    Date of Birth:
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="dobInput"
                    defaultValue={user.dob}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="genderInput" className="form-label">
                    Gender :
                  </label>
                  {isEditable ? (
                    <select
                      className="form-select"
                      id="genderInput"
                      defaultValue={user.gender}
                      readOnly={!isEditable}
                    >
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Others">Others</option>
                      <option value="Prefer Not To Say">
                        Prefer Not To Say
                      </option>
                    </select>
                  ) : (
                    <input
                      type="text"
                      className="form-control"
                      id="genderInput"
                      defaultValue={user.gender}
                      readOnly={!isEditable}
                    />
                  )}
                </div>
                <div className="mb-3">
                  <label htmlFor="departmentInput" className="form-label">
                    Department :
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="departmentInput"
                    defaultValue={user.department}
                    readOnly={!isEditable}
                  />
                </div>
                <div className="text-center mt-3">
                  <div className="d-flex justify-content-between align-items-center">
                    <div className="btn-container">
                      <a
                        href="#"
                        className={`btn btn-primary ${
                          isEditable ? "slideRight" : "slideLeft"
                        }`}
                        onClick={handleEditClick}
                      >
                        {isEditable ? "Save Changes" : "Edit Profile"}
                      </a>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
