import React from "react";
import { useNavigate } from "react-router-dom";
import "./profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem("token"); // Check the user's authentication status

  // If the user is not authenticated, redirect them to the login page
  if (!isAuthenticated) {
    navigate("/login");
  }

  // Sample user information (you can replace this with actual user data)
  const user = {
    name: "Mohit Das",
    address: "Subhashnagar, Barasat, Kolkata - 700121",
    phoneNumber: "+91 7003961588",
    age: "20",
    gender: "Male",
    department: "BCA",
    // Add more user details here
  };

  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-md-4">
          {/* Left section with user image */}
          {/* ... (existing code) */}
        </div>
        <div className="col-md-8">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">My Profile</h2>
              <form>
                <div className="mb-3">
                  <label htmlFor="nameInput" className="form-label">
                    Name:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nameInput"
                    defaultValue={user.name}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="addressInput" className="form-label">
                    Address:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="addressInput"
                    defaultValue={user.address}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="phoneNumberInput" className="form-label">
                    Phone Number:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="phoneNumberInput"
                    defaultValue={user.phoneNumber}
                  />
                </div>
                {/* Add more input fields for other user details */}
                <a href="#" className="btn btn-primary">
                  Edit Profile
                </a>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
