import 'bootstrap/dist/css/bootstrap.min.css';
import { Routes, Route, Navigate } from "react-router-dom";
import Header from "./components/Headers/Headers";
import Footers from './components/Footers/Footer';
import Home from "./pages/Home/Home";
import Register from "./pages/Register/Register";
import Edit from "./pages/Edit/Edit";
import Profile from "./pages/Profile/Profile";
import Tables from './pages/L&F Items/L&F';
import Home1 from './pages/Home1/Home1';
import Contact from './pages/Contact/Contact';
import Main from './components/Main';
import Signup from './components/Signup';
import Login from './components/Login';
import EmailVerify from './components/EmailVerify';

function App() {
  const user = localStorage.getItem("token");
  console.log("Token in localStorage:", user);
  return (
    <>
      <Header />

      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/register' element={<Register />} />
        <Route path='/edit/:id' element={<Edit />} />
        <Route path='/userprofile/:id' element={<Profile />} />
        <Route path='/lost&found/:id' element={<Tables />} />
        <Route path='/Home1' element={<Home1 />} />
        <Route path='/Contact' element={<Contact />} />
        <Route path="/login" element={<Login />} />
        {/* Only allow access to Main if the user is authenticated */}
        {user ? (
          <Route path="/Main" element={<Main />} />
        ) : (
          <Route path="/Login" element={<Login />} />
        )}

        <Route path="/signup" element={<Signup />} />
        <Route path="/" element={<Navigate replace to="/login" />} />
        <Route path="/users/:id/verify/:token" element={<EmailVerify />} />
        <Route path="/users/" />
      </Routes>

      <Footers />
    </>
  );
}

export default App;
