// App.js
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import { Routes, Route } from "react-router-dom";
// import Header from "./components/Headers/Headers";
import Footers from "./components/Footers/Footer";
import Navigation from "./components/Navbar/Navigation";
import Home1 from "./pages/Home1/Home1";
import Contact from "./pages/Contact/Contact";
import Login from "./components/Login";
import Register from "./pages/Register/Register";
import Profile from "./pages/Profile/Profile";
import Item from "./pages/SingleItem/singleItem";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem("token")
  );

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
  };

  return (
    <>
      <Header />
      <Navigation
        isAuthenticated={isAuthenticated}
        handleLogout={handleLogout}
      />
      <Routes>
        <Route path="/" element={<Home1 />} />
        <Route path="/login" element={<Login />} />
        {isAuthenticated && <Route path="/register" element={<Register />} />}
        {isAuthenticated && <Route path="/profile" element={<Profile />} />}
        {isAuthenticated && <Route path="/ItemList" element={<Item />} />}
        {isAuthenticated && <Route path="/lost&found/:id" element={<Item />} />}
        {isAuthenticated && <Route path="/contact" element={<Contact />} />}
      </Routes>
      <Footers />
    </>
  );
}

export default App;
