import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Select from "react-select";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./register.css";

// ... (previous code)

const AddItem = () => {
  const [inputData, setInputData] = useState({
    itemName: "",
    category: "", // Add this field for item category
    description: "",
    location: "",
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  // Additional fields based on categories
  const [color, setColor] = useState("");
  const [model, setModel] = useState("");
  const [brand, setBrand] = useState("");
  const [type, setType] = useState("");
  const [cardType, setCardType] = useState("");

  const categoryOptions = [
    { value: "Watch", label: "Watch" },
    { value: "Purse/Money Bag", label: "Purse/Money Bag" },
    { value: "Cards", label: "Cards" },
    { value: "Phone", label: "Phone" },
    { value: "Phone Cover", label: "Phone Cover" },
    { value: "Earphones", label: "Earphones" },
    { value: "Books", label: "Books" },
    {
      value: "Keyboard/Mouse/Other Similar Gadgets",
      label: "Keyboard/Mouse/Other Similar Gadgets",
    },
    { value: "Charger", label: "Charger" },
    { value: "Glasses/Spectacles", label: "Glasses/Spectacles" },
    { value: "Speaker", label: "Speaker" },
    { value: "Lunch Box/Bottle", label: "Lunch Box/Bottle" },
    { value: "Keys", label: "Keys" },
    {
      value: "Pen Drive/USB/Similar Gadgets",
      label: "Pen Drive/USB/Similar Gadgets",
    },
    { value: "Make-up Kit", label: "Make-up Kit" },
    {
      value: "Jewellery/Chains/Rings/Similar Items",
      label: "Jewellery/Chains/Rings/Similar Items",
    },
    { value: "Outfit", label: "Outfit" },
    { value: "Others", label: "Others" },
  ];

  const setInputValue = (e) => {
    const { name, value } = e.target;
    setInputData({ ...inputData, [name]: value });
  };

  const setCategoryValue = (selectedCategory) => {
    setInputData({ ...inputData, category: selectedCategory.value });
  };

  const setProfile = (e) => {
    const file = e.target.files[0];
    setImage(file);

    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const submitItemData = async (e) => {
    e.preventDefault();

    const { itemName, category, description, location } = inputData;

    if (!itemName || !category || !description || !location || !image) {
      toast.error("Please fill out all the required fields.");
    } else {
      // Combine all data including additional details
      const itemData = {
        itemName,
        category,
        description,
        location,
        image,
        color, // Add additional fields here
        model,
        brand,
        type,
        cardType,
      };

      // Handle the form submission, e.g., send the data to your server
      console.log(itemData); // For demonstration purposes
      toast.success("Item has been successfully added.");
    }
  };

  const renderAdditionalFields = () => {
    if (inputData.category === "Watch") {
      return (
        <>
          <Form.Group className="mb-3 col-lg-6" controlId="formBrand">
            <Form.Label>Brand</Form.Label>
            <Form.Control
              type="text"
              name="brand"
              value={brand}
              onChange={(e) => setBrand(e.target.value)}
              placeholder="Enter Brand"
            />
          </Form.Group>

          <Form.Group className="mb-3 col-lg-6" controlId="formModel">
            <Form.Label>Model</Form.Label>
            <Form.Control
              type="text"
              name="model"
              value={model}
              onChange={(e) => setModel(e.target.value)}
              placeholder="Enter Model"
            />
          </Form.Group>

          <Form.Group className="mb-3 col-lg-6" controlId="formColor">
            <Form.Label>Color</Form.Label>
            <Form.Control
              type="text"
              name="color"
              value={color}
              onChange={(e) => setColor(e.target.value)}
              placeholder="Enter Color"
            />
          </Form.Group>

          <Form.Group className="mb-3 col-lg-6" controlId="formType">
            <Form.Label>Type</Form.Label>
            <Form.Control
              type="text"
              name="type"
              value={type}
              onChange={(e) => setType(e.target.value)}
              placeholder="Enter Type"
            />
          </Form.Group>
        </>
      );
    } else if (inputData.category === "Cards") {
      return (
        <>
          <Form.Group className="mb-3 col-lg-6" controlId="formCardType">
            <Form.Label>Card Type</Form.Label>
            <Form.Control
              type="text"
              name="cardType"
              value={cardType}
              onChange={(e) => setCardType(e.target.value)}
              placeholder="Enter Card Type"
            />
          </Form.Group>

          {/* Add more fields specific to the Cards category here */}
        </>
      );
    }
    return null;
  };

  useEffect(() => {
    if (image) {
      const reader = new FileReader();
      reader.onload = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(image);
    } else {
      setPreview(null);
    }
  }, [image]);

  return (
    <div className="container">
      <h2 className="text-center mt-1">Add Lost or Found Item</h2>
      <Card className="shadow mt-3 p-3">
        <Form>
          {/* Basic Details section */}
          <div className="basic-details-section">
            <h3 className="text-center">Basic Details</h3>
            <Row>
              <Form.Group className="mb-3 col-lg-6" controlId="formItemName">
                <Form.Label>Item Name<span className="text-danger">*</span></Form.Label>
                <Form.Control
                  type="text"
                  name="itemName"
                  value={inputData.itemName}
                  onChange={setInputValue}
                  placeholder="Enter Item Name"
                />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formCategory">
                <Form.Label>Category<span className="text-danger">*</span></Form.Label>
                <Select
                  options={categoryOptions}
                  value={categoryOptions.find(
                    (option) => option.value === inputData.category
                  )}
                  onChange={setCategoryValue}
                  placeholder="Select Category"
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formDescription">
                <Form.Label>Description<span className="text-danger">*</span></Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="description"
                  value={inputData.description}
                  onChange={setInputValue}
                  placeholder="Enter Item Description"
                />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formLocation">
                <Form.Label>Location<span className="text-danger">*</span></Form.Label>
                <Form.Control
                  type="text"
                  name="location"
                  value={inputData.location}
                  onChange={setInputValue}
                  placeholder="Enter Location"
                />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formItemType">
                <Form.Label className="d-block">Item Type</Form.Label>
                <div className="d-flex align-items-center">
                  <div className="mr-3">
                    <Form.Check
                      type="radio"
                      label="Lost"
                      name="itemType"
                      value="Lost"
                      checked={inputData.itemType === "Lost"}
                      onChange={setInputValue}
                    />
                  </div>
                  <div className="mr-6">
                    {" "}
                    {/* Add margin-right for gap */}
                    <Form.Check
                      type="radio"
                      label="Found"
                      name="itemType"
                      value="Found"
                      checked={inputData.itemType === "Found"}
                      onChange={setInputValue}
                    />
                  </div>
                </div>
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formItemImage">
                <Form.Label>Item Image</Form.Label>
                <Form.Control
                  type="file"
                  name="item_image"
                  onChange={setProfile}
                />
              </Form.Group>
            </Row>
          </div>
          {inputData.category /* Check if a category is selected */ && (
            <hr className="divider" /> /* Add a divider when a category is selected */
          )}
          {inputData.category === "Watch" && (
            <div className="additional-details-section">
              <h3 className="text-center">Additional Details</h3>
              <Row>
                <Form.Group className="mb-3 col-lg-6" controlId="formColor">
                  <Form.Label>Color</Form.Label>
                  <Form.Control
                    type="text"
                    name="color"
                    value={inputData.color}
                    onChange={setInputValue}
                    placeholder="Enter Color"
                  />
                </Form.Group>

                <Form.Group className="mb-3 col-lg-6" controlId="formModel">
                  <Form.Label>Model</Form.Label>
                  <Form.Control
                    type="text"
                    name="model"
                    value={inputData.model}
                    onChange={setInputValue}
                    placeholder="Enter Model"
                  />
                </Form.Group>

                <Form.Group className="mb-3 col-lg-6" controlId="formBrand">
                  <Form.Label>Brand</Form.Label>
                  <Form.Control
                    type="text"
                    name="brand"
                    value={inputData.brand}
                    onChange={setInputValue}
                    placeholder="Enter Brand"
                  />
                </Form.Group>

                <Form.Group className="mb-3 col-lg-6" controlId="formType">
                  <Form.Label>Type (Analog/Digital)</Form.Label>
                  <Form.Control
                    type="text"
                    name="type"
                    value={inputData.type}
                    onChange={setInputValue}
                    placeholder="Enter Type"
                  />
                </Form.Group>
              </Row>
            </div>
          )}
          <div className="d-flex justify-content-center">
            <Button variant="primary" type="submit" onClick={submitItemData}>
              Submit
            </Button>
          </div>
        </Form>
      </Card>
      <ToastContainer position="top-center" />
    </div>
  );
};

export default AddItem;
